import React from 'react';
import { useSelector } from 'react-redux';
import { motion } from 'framer-motion';
import { 
  LayoutDashboard, 
  Receipt, 
  CheckCircle, 
  Users, 
  Bell, 
  Settings, 
  User,
  Building2,
  ChevronRight
} from 'lucide-react';
import { cn } from '../../lib/utils';

const Sidebar = () => {
  const { sidebarOpen } = useSelector((state) => state.ui);
  const { user } = useSelector((state) => state.auth);

  const navigation = [
    { name: 'Dashboard', href: '/dashboard', icon: LayoutDashboard },
    { name: 'Expenses', href: '/expenses', icon: Receipt },
    { name: 'Approvals', href: '/approvals', icon: CheckCircle },
    ...(user?.role === 'admin' ? [{ name: 'Users', href: '/users', icon: Users }] : []),
    { name: 'Notifications', href: '/notifications', icon: Bell },
    { name: 'Profile', href: '/profile', icon: User },
    { name: 'Settings', href: '/settings', icon: Settings },
  ];

  return (
    <motion.div
      initial={false}
      animate={{ width: sidebarOpen ? '256px' : '64px' }}
      className="bg-card border-r border-border transition-all duration-300"
    >
      <div className="flex flex-col h-full">
        <div className="p-4">
          <div className="flex items-center">
            <Building2 className="h-8 w-8 text-primary" />
            {sidebarOpen && (
              <motion.span
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                className="ml-2 text-lg font-semibold text-foreground"
              >
                ExpenseApp
              </motion.span>
            )}
          </div>
        </div>
        
        <nav className="flex-1 px-2 py-4">
          <ul className="space-y-1">
            {navigation.map((item) => (
              <li key={item.name}>
                <a
                  href={item.href}
                  className="flex items-center px-3 py-2 text-sm font-medium rounded-lg text-muted-foreground hover:text-foreground hover:bg-accent transition-colors"
                >
                  <item.icon className="h-5 w-5" />
                  {sidebarOpen && (
                    <motion.span
                      initial={{ opacity: 0 }}
                      animate={{ opacity: 1 }}
                      className="ml-3"
                    >
                      {item.name}
                    </motion.span>
                  )}
                </a>
              </li>
            ))}
          </ul>
        </nav>
      </div>
    </motion.div>
  );
};

export default Sidebar;
